import { useSelector } from "react-redux"
import style from "../ByName/ByName.module.css"


const ByName = () => {


  const dogs = useSelector((state) => state.dogsByName);
  const alldogs = useSelector((state) => state.dogs);

  if (!Array.isArray(dogs) || dogs.length === 0) {
    return <h1>Cargando...</h1>;
  }

  // const filteredDog = alldogs.find((dog) => dog.id === Number(id));


  return (
    <div className={style.container}>
      <h1 className={style.titulo}>DETALLE POR NOMBRE</h1>
      {/* {filteredDog && <img src={filteredDog.imagen} alt="Imagen" />} */}
      <h1>ID : {dogs[0].id}</h1>
      <h1>NOMBRE : {dogs[0].nombre}</h1>
      {/* <h1>IMAGEN : {dogs[0].imagen}</h1> */}
      <h1>ALTURA : {dogs[0].altura} Cms.</h1>
      <h1>PESO : {dogs[0].peso} Kgs.</h1>
      <h1>TEMPERAMENTOS : {dogs[0].temperamentos}</h1>
      <h1>AÑOS DE VIDA : {dogs[0].añosDeVida}</h1>

    </div>
  )


}

export default ByName
