import {
  GET_ALL_DOGS, GET_DOG_BY_ID, GET_DOGS_BY_NAME,
  SET_TEMPERAMENT_FILTER, SET_ORIGIN_FILTER,
  SET_RAZE_ORDER, SET_RAZE_BYWEIGTH_ORDER
} from "./actions";


const initialState = {
  dogs: [],
  dogDetail:{nombre:"juan"},
  dogId: {},
  dogsByName: []
};


const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_ALL_DOGS:
      return { ...state, dogs: action.payload }





    case GET_DOG_BY_ID:
      return { ...state, dogDetail: action.payload }





      
    case GET_DOGS_BY_NAME:
      return { ...state, dogByName: action.payload }






    case SET_RAZE_ORDER:
      let copyDogs = [...state.dogs];
      if (action.payload === 'A') {
        copyDogs.sort((a, b) => a.nombre.localeCompare(b.nombre));
      } else if (action.payload === 'D') {
        copyDogs.sort((a, b) => b.nombre.localeCompare(a.nombre));
      }
      return { ...state, dogs: copyDogs };





    case SET_RAZE_BYWEIGTH_ORDER:
      let copyDogs1 = [...state.dogs];
      if (action.payload === 'A') {
        copyDogs1.sort((a, b) => a.nombre.localeCompare(b.nombre));
      } else if (action.payload === 'D') {
        copyDogs1.sort((a, b) => b.nombre.localeCompare(a.nombre));
      }
      return { ...state, dogs: copyDogs1 };

      


    case SET_TEMPERAMENT_FILTER:
      let copyDogs2 = state.dogs.filter((dog) => {
        return dog.temperamento === action.payload
      })
      return { ...state, dogs: copyDogs }




      case SET_ORIGIN_FILTER:
  const { payload } = action;
  let filteredDogs = [];
  
  if (payload === "API") {
    filteredDogs = state.dogs.filter((dog) => !isNaN(dog.id));
  } else if (payload === "BDD") {
    filteredDogs = state.dogs.filter((dog) => isNaN(dog.id));
  } else if (payload === "ALL") {
    
    filteredDogs = state.dogs;
  } else {
   
    return state;
  }

  
  return { ...state, dogs: filteredDogs };

      







    default:
      return { ...state };
  }
}

export default rootReducer;
