import style from "./Form.module.css";
import { useState } from "react";
import axios from "axios";
import { useSelector } from "react-redux";

const Form = () => {
  const temperaments = useSelector((state) => state.allTemperaments);

  const [form, setForm] = useState({
    nombre: "",
    altura: "",
    peso: "",
    añosDeVida: "",
    temperamentos: "",
  });

  const [errorsState, setErrors] = useState({
    nombre: "",
    altura: "",
    peso: "",
    añosDeVida: "",
    temperamentos: "",
  });

  const onChange = (e) => {
    const property = e.target.name;
    const value = e.target.value;

    setErrors(validate({ ...form, [property]: value }, errorsState));
    setForm({ ...form, [property]: value });
  };

  const validate = (form, errorsState) => {
    // ... código de validación existente ...
  };

  const submitHandler = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:3001/dogs", form)
      .then((res) => alert(res))
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h1 className={style.titulo}>CREAR RAZA</h1>
      <form onSubmit={submitHandler} className={style.form}>
        {/* ... otros campos de entrada existentes ... */}
        <div>
          <label>TEMPERAMENTOS: </label>
          <select
            name="temperamentos"
            value={form.temperamentos}
            onChange={onChange}
          >
            <option value="">Seleccione un temperamento</option>
            {temperaments.map((temperamento) => (
              <option key={temperamento} value={temperamento}>
                {temperamento}
              </option>
            ))}
          </select>
          <span>{errorsState.temperamentos}</span>
        </div>
        {/* ... botón de envío existente ... */}
      </form>
    </div>
  );
};

export default Form;
