import { useEffect } from "react";
import CardsContainer from "../../components/CardsContainer/CardsContainer"
import { useDispatch } from "react-redux";
import style from "./Home.module.css"
import {
  getAllDogs, setRazeOrder, setRazeOrderByWeight,
  setTempFilter, setOriginFilter,
} from "../../redux/actions";



const Home = () => {


  const dispatch = useDispatch()


  useEffect(() => {
    dispatch(getAllDogs())
  }, [dispatch])



  const handleOrder = (e) => {
    dispatch(setRazeOrder(e.target.value));
  };



  const handleOrderByWeight = (e) => {
    dispatch(setRazeOrderByWeight(e.target.value));
  };



  
  const handleFilterByTemp = (e) => {
    dispatch(setTempFilter(e.target.value));
  };



  const handleFilterByOrigin = (e) => {
    dispatch(setOriginFilter(e.target.value));
  };




  return (



    <div className={style.container}>

      <div className={style.container}>
        <select onChange={handleOrder} >
          <option value="">ORDEN ALFABETICO</option>
          <option value="A">Ascendente</option>
          <option value="D">Descendente</option>
        </select>

        <select onChange={handleOrderByWeight}>
          <option value="">ORDEN POR PESO</option>
          <option value="A">Ascendente</option>
          <option value="D">Descendente</option>
        </select>

        <select onChange={handleFilterByTemp}>
          <option value="">TEMPERAMENTOS</option>
          <option value="ALL">Todos</option>
          <option value="Adventurous">Adventurous</option>
          <option value="Affectionatee">Affectionate</option>
          <option value="Aggressive">Aggressive</option>
          <option value="Agile">Agile</option>
          <option value="Alert">Alert</option>
          <option value="Aloof">Aloof</option>
          <option value="Amiable">Amiable</option>
          <option value="Assertive">Assertive</option>
          <option value="Athletic">Athletic</option>
          <option value="Attentive">Attentive</option>
          <option value="Benevolent">Benevolent</option>
          <option value="Bold">Bold</option>
        </select>

        <select onChange={handleFilterByOrigin}>
          <option value="ALL">TODOS</option>
          <option value="BDD">BDD</option>
          <option value="API">API</option>
        </select>
      </div>


      <h1 className={style.titulo}>DOGS HOME</h1>
      <CardsContainer />
    </div>


  )
}


export default Home;
